package Assignment1;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 10;
		
		//to check if num is odd or even
		boolean toCheck = (num % 2 == 0)? true: false;
		//if the num is even --> true
		//if the num is odd --> false
		System.out.println(toCheck);
	}

}
